# Window Move Function
def get_position(evn):
    xwin = brain.winfo_x()
    ywin = brain.winfo_y()

    rtp_x = evn.x_root
    rtp_y = evn.y_root

    xwin = xwin - rtp_x
    ywin = ywin - rtp_y

    def move_fun(e):
        brain.geometry('+{0}+{1}'.format(e.x_root + xwin, e.y_root + ywin))

    title_bar.bind('<B1-Motion>', move_fun)

title_bar.bind('<Button-1>', get_position)

