def downloader():
    global downloader_thread_event
    global line_count_for_logger
    global ask_to_open_folder
    global check_emoji
    global if_all_file_exist

    while True:

        downloader_thread_event.wait()

        # Define Default Path
        empty_check = f'{os.getcwd()[:2]}/'

        # Save-As Path Selector
        location = ask_to_open_folder

        # Link Sanitization - 1
        link = None
        link = str(lnk_entry.get()).strip()

        # Link Input Check
        if link == '':
            logger.insert(line_count_for_logger, '‚ùå Link Not Selected')
            logger.see(line_count_for_logger)
            line_count_for_logger += 1
            downloader_thread_event.clear()
            continue
        
        # Folder Selection Check
        if location.rstrip() == empty_check:
            logger.insert(line_count_for_logger, '‚ùå Save-As Folder Not Selected')
            logger.see(line_count_for_logger)
            line_count_for_logger += 1
            downloader_thread_event.clear()
            continue
        
        # Single Video Downloader
        if 'watch' in link or 'youtu.be' in link:
            
            # If used Shared link
            if 'youtube.be' in link or 'youtu.be' in link:
                link = str('https://www.youtube.com/watch?v=' + link.split('/')[-1])

            # If a Mix Video
            if '&' in link:
                link = str(link.split('&')[0])

            # Id Seperator
            checker_id = str(link.split('=')[-1])

            # Live Error Checker and Unavailable Video Checkers

            while True:
                try:
                    api_service_name = "youtube"
                    api_version = "v3"
                    api_key = 'AIzaSyCC9iU8G29-YuVsfaxxyWXlfw223oRidM8'

                    youtube = build(api_service_name, api_version, developerKey=api_key)

                    request = youtube.videos().list(part="snippet",id=str(checker_id))
                    response = request.execute()
                    break
                except TimeoutError:
                    response = None
                    s(2)
                    continue
            
            try:
                if str(response['items'][0]['snippet']['liveBroadcastContent']) == 'live':
                    logger.insert(line_count_for_logger, f"‚ùå Live Stream Can't Be Downloaded")
                    logger.see(line_count_for_logger)
                    line_count_for_logger += 1
                    downloader_thread_event.clear()
                    continue
            except IndexError:
                logger.insert(line_count_for_logger, f'‚ùå Video is Unavailable')
                logger.see(line_count_for_logger)
                line_count_for_logger += 1
                downloader_thread_event.clear()
                continue

            # Options For Audio
            ydl_opts = {
            'format': '140',
            'outtmpl': location + '\\' + '/%(id)s.%(ext)s',
            'keepvideo': 'False',
            'socket_timeout':5,
            'quiet': 'True',
            'no_warnings': 'True'
            }

            single_vid_link = link.strip() # Link Sanitization - 2

            # Pre Video Info Extractor
            while True:
                try:
                    single_vid_info = youtube_dl.YoutubeDL(ydl_opts).extract_info(single_vid_link, download=False)
                    break
                except youtube_dl.utils.DownloadError:
                    s(1)
                    continue
            
            title = str(single_vid_info['title']) # Dynamic Video Title
            vidid = str(link).split('=')[-1] # Dynamic Video Id
            ext = str(single_vid_info['ext']) # Dynamic Video Extension 

            # File Name Sanitization
            if '\\' in title:
                title = title.replace('\\', '')
            if '/' in title:
                title = title.replace('/', '')
            if ':' in title:
                title = title.replace(':', '')
            if '*' in title:
                title = title.replace('*', '')
            if '?' in title:
                title = title.replace('?', '')
            if '"' in title:
                title = title.replace('"', '')
            if '<' in title:
                title = title.replace('<', '')
            if '>' in title:
                title = title.replace('>', '')
            if '|' in title:
                title = title.replace('|', '')

            # Pre File Existance Checker
            mp3_with_file_name = Path(f'{location}\\{title}.mp3')
            if mp3_with_file_name.exists():
                logger.insert(line_count_for_logger, f'‚ôªÔ∏è File Already Exist')
                logger.see(line_count_for_logger)
                line_count_for_logger += 1
                downloader_thread_event.clear()
                continue
            
            # Download Initiation Logging
            logger.insert(line_count_for_logger, f'üì• Downloading {title}')
            logger.see(line_count_for_logger)
            line_count_for_logger += 1

            # Downloader Block
            err = False

            while True:
                try:
                    youtube_dl.YoutubeDL(ydl_opts).extract_info(single_vid_link)
                    break
                except youtube_dl.utils.DownloadError:
                    s(1)
                    continue
                except:
                    err = True
                    break
                    
            if err == True:
                err = False
                logger.insert(line_count_for_logger, f"‚ùå Video Can't be Downloaded")
                logger.see(line_count_for_logger)
                line_count_for_logger += 1
                downloader_thread_event.clear()
                continue

            # Id Titled MP3
            m4a_with_file_id = f'{location}\\{vidid}.{ext}'
            # Name Titled MP3
            mp3_with_file_name = f'{location}\\{title}.mp3'

            # Rename With Name Titled MP3
            if os.path.exists(m4a_with_file_id):
                os.replace(m4a_with_file_id, mp3_with_file_name)

            # Download Done Logging
            logger.insert(line_count_for_logger, f'{check_emoji} Downloaded {title}')
            logger.see(line_count_for_logger)
            line_count_for_logger += 1

        elif 'playlist' in link:
            playlist_id = str(link.split('=')[-1])

            # Playlist Link Extractor and Check for Private PlayList

            try:
                #=========================== Reset Block ===========================#
                api_service_name = "youtube"                                        #
                api_version = "v3"                                                  #
                api_key = 'AIzaSyCC9iU8G29-YuVsfaxxyWXlfw223oRidM8'                 #
                                                                                    #
                youtube = build(api_service_name, api_version, developerKey=api_key)#
                                                                                    #
                request = youtube.playlistItems().list(                             #
                    part = "snippet",                                               #
                    playlistId = 'PLxzfK8zA4FNBQzS84AK_0evLIdu0u-AhG',              #
                    maxResults = 5000                                               #
                )                                                                   #
                response = request.execute()                                        #
                #=========================== Reset Block ===========================#

                request = youtube.playlistItems().list(
                    part = "snippet",
                    playlistId = playlist_id,
                    maxResults = 5000
                )
                response = request.execute()

            except googleapiclient.errors.HttpError:
                logger.insert(line_count_for_logger, '‚ùå Playlist is not Public')
                logger.see(line_count_for_logger)
                line_count_for_logger += 1

                #================ Reset Block ========================#
                request = youtube.playlistItems().list(               #
                    part = "snippet",                                 #
                    playlistId = 'PLxzfK8zA4FNBQzS84AK_0evLIdu0u-AhG',#
                    maxResults = 5000                                 #
                )                                                     #
                response = request.execute()                          #
                #================ Reset Block ========================#

                downloader_thread_event.clear()
                continue
            
            public_err = False
            playlist_items = []
            while request is not None:
                try:
                    response = request.execute()
                    playlist_items += response["items"]
                    request = youtube.playlistItems().list_next(request, response)
                except googleapiclient.errors.HttpError:
                    #=================== Reset Block =====================#
                    request = youtube.playlistItems().list(               #
                        part = "snippet",                                 #
                        playlistId = 'PLxzfK8zA4FNBQzS84AK_0evLIdu0u-AhG',#
                        maxResults = 5000                                 #
                    )                                                     #
                    response = request.execute()                          #
                    public_err = True                                     #
                    break                                                 #
                    #=================== Reset Block =====================#

            if public_err == True:
                public_err = False
                downloader_thread_event.clear()
                continue

            playlist_url = []
            for t in playlist_items:
                playlist_url.append(f'https://www.youtube.com/watch?v={t["snippet"]["resourceId"]["videoId"]}')

            # Dublicate Excluder
            for ids in playlist_url:
                indi_vid_link = str(ids).split('=')[-1]
                indi_vid_link = f'https://www.youtube.com/watch?v={indi_vid_link}'
                if indi_vid_link not in playlist_url:
                    playlist_url.append(indi_vid_link)

            # PlayList Download Initiation Logging
            file_count_val = len(playlist_url)
            if file_count_val == 1:
                file_count = str(len(playlist_url)) + " File"
            elif file_count_val > 1:
                file_count = str(len(playlist_url)) + " Files"

            logger.insert(line_count_for_logger, f'üì•üì• Downloading PlayList ({file_count})')
            logger.see(line_count_for_logger)
            line_count_for_logger += 1

            for link in playlist_url:
                # Id Seperator
                checker_id = str(link.split('=')[-1])
                
                # Live Error Checker and Unavailable Video Checker
                while True:
                    try:
                        api_service_name = "youtube"
                        api_version = "v3"
                        api_key = 'AIzaSyCC9iU8G29-YuVsfaxxyWXlfw223oRidM8'

                        youtube = build(api_service_name, api_version, developerKey=api_key)

                        request = youtube.videos().list(part="snippet",id=str(checker_id))
                        response = request.execute()
                        break
                    except TimeoutError:
                        response = None
                        s(2)
                        continue
                
                try:
                    if str(response['items'][0]['snippet']['liveBroadcastContent']) == 'live':
                        logger.insert(line_count_for_logger, f"‚ùå Live Stream Can't Be Downloaded")
                        logger.see(line_count_for_logger)
                        line_count_for_logger += 1
                        continue
                except IndexError:
                    logger.insert(line_count_for_logger, f'‚ùå Video is Unavailable')
                    logger.see(line_count_for_logger)
                    line_count_for_logger += 1
                    continue

                # Options For Audio
                ydl_opts = {
                'format': '140',
                'outtmpl': location + '\\' + '/%(id)s.%(ext)s',
                'keepvideo': 'False',
                'socket_timeout':5,
                'quiet': 'True',
                'no_warnings': 'True'
                }

                single_vid_link = link.strip() # Link Sanitization - 2
                # Pre Video Info Extractor
                while True:
                    try:
                        single_vid_info = youtube_dl.YoutubeDL(ydl_opts).extract_info(single_vid_link, download=False)
                        break
                    except youtube_dl.utils.DownloadError:
                        s(1)
                        continue
                
                title = str(single_vid_info['title']) # Dynamic Video Title
                vidid = str(link).split('=')[-1] # Dynamic Video Id
                ext = str(single_vid_info['ext']) # Dynamic Video Extension 

                # File Name Sanitization
                if '\\' in title:
                    title = title.replace('\\', '')
                if '/' in title:
                    title = title.replace('/', '')
                if ':' in title:
                    title = title.replace(':', '')
                if '*' in title:
                    title = title.replace('*', '')
                if '?' in title:
                    title = title.replace('?', '')
                if '"' in title:
                    title = title.replace('"', '')
                if '<' in title:
                    title = title.replace('<', '')
                if '>' in title:
                    title = title.replace('>', '')
                if '|' in title:
                    title = title.replace('|', '')

                # Pre File Existance Checker
                mp3_with_file_name = Path(f'{location}\\{title}.mp3')
                if mp3_with_file_name.exists():
                    logger.insert(line_count_for_logger, f'‚ôªÔ∏è File Already Exist')
                    logger.see(line_count_for_logger)
                    if_all_file_exist += 1
                    line_count_for_logger += 1
                    continue

                # Download Initiation Logging
                logger.insert(line_count_for_logger, f'üì• Downloading {title}')
                logger.see(line_count_for_logger)
                line_count_for_logger += 1
                
                # Downloader Block
                err = False

                while True:
                    try:
                        youtube_dl.YoutubeDL(ydl_opts).extract_info(single_vid_link)
                        break
                    except youtube_dl.utils.DownloadError:
                        s(1)
                        continue
                    except:
                        err = True
                        break
                        
                if err == True:
                    err = False
                    logger.insert(line_count_for_logger, f"‚ùå Video Can't be Downloaded")
                    logger.see(line_count_for_logger)
                    line_count_for_logger += 1
                    downloader_thread_event.clear()
                    continue

                # Id Titled MP3
                m4a_with_file_id = f'{location}\\{vidid}.{ext}'
                # Name Titled MP3
                mp3_with_file_name = f'{location}\\{title}.mp3'

                # Rename With Name Titled MP3
                if os.path.exists(m4a_with_file_id):
                    os.replace(m4a_with_file_id, mp3_with_file_name)
                
                # Download Done Logging
                logger.insert(line_count_for_logger, f'{check_emoji} Downloaded {title}')
                logger.see(line_count_for_logger)
                line_count_for_logger += 1

            # PlayList Download Done Logging
            if if_all_file_exist == file_count_val:
                logger.insert(line_count_for_logger, f'{check_emoji}{check_emoji} All File Exist')
                logger.see(line_count_for_logger)
                line_count_for_logger += 1
            else:
                logger.insert(line_count_for_logger, f'{check_emoji}{check_emoji} PlayList Downloaded')
                logger.see(line_count_for_logger)
                line_count_for_logger += 1

        else:
            logger.insert(line_count_for_logger, f"‚ùå Link is Not Valid")
            logger.see(line_count_for_logger)
            line_count_for_logger += 1
            downloader_thread_event.clear()
            continue

        downloader_thread_event.clear()

